import unittest

from .test_canvas import TestCanvas
from .test_cartesian import TestCartesian2Axis
from .test_circle import TestCircle
from .test_custom_drawable_names import TestCustomDrawableNames
from .test_drawable_name_generator import TestDrawableNameGenerator
from .test_ellipse import TestEllipse
from .test_expression_validator import TestExpressionValidator
from .test_function import TestFunction
from .test_function_calling import TestProcessFunctionCalls
from .test_math_functions import TestMathFunctions
from .test_point import TestPoint
from .test_position import TestPosition
from .test_rectangle import TestRectangle
from .test_segment import TestSegment
from .test_triangle import TestTriangle
from .test_vector import TestVector


def run_tests():
    suite = unittest.TestSuite()
    loader = unittest.TestLoader()
    suite.addTest(loader.loadTestsFromTestCase(TestMathFunctions))
    suite.addTest(loader.loadTestsFromTestCase(TestDrawableNameGenerator))
    suite.addTest(loader.loadTestsFromTestCase(TestPosition))
    suite.addTest(loader.loadTestsFromTestCase(TestPoint))
    suite.addTest(loader.loadTestsFromTestCase(TestSegment))
    suite.addTest(loader.loadTestsFromTestCase(TestVector))
    suite.addTest(loader.loadTestsFromTestCase(TestTriangle))
    suite.addTest(loader.loadTestsFromTestCase(TestRectangle))
    suite.addTest(loader.loadTestsFromTestCase(TestCircle))
    suite.addTest(loader.loadTestsFromTestCase(TestEllipse))
    suite.addTest(loader.loadTestsFromTestCase(TestFunction))
    suite.addTest(loader.loadTestsFromTestCase(TestCartesian2Axis))
    suite.addTest(loader.loadTestsFromTestCase(TestCanvas))
    suite.addTest(loader.loadTestsFromTestCase(TestExpressionValidator))
    suite.addTest(loader.loadTestsFromTestCase(TestProcessFunctionCalls))
    suite.addTest(loader.loadTestsFromTestCase(TestCustomDrawableNames))
    runner = unittest.TextTestRunner()
    runner.run(suite)