import math


class Utilities:
    @classmethod
    def get_2D_distance(cls, point1, point2):
        dx = point1.x - point2.x
        dy = point1.y - point2.y
        distance = math.sqrt(dx**2 + dy**2)
        return distance

    @classmethod
    def get_2D_midpoint(cls, point1, point2):
        x = (point1.x + point2.x) / 2
        y = (point1.y + point2.y) / 2
        return (x, y)

    @classmethod
    def get_triangle_area(cls, point1, point2, point3):
        # Calculate the area of the triangle using Heron's formula
        a = cls.get_2D_distance(point1, point2)
        b = cls.get_2D_distance(point2, point3)
        c = cls.get_2D_distance(point3, point1)
        s = (a + b + c) / 2
        area = math.sqrt(s * (s - a) * (s - b) * (s - c))
        return area
    
    @classmethod
    def get_rectangle_area(cls, point1, point2):
        width = abs(point1.x - point2.x)
        height = abs(point1.y - point2.y)
        area = width * height
        return area